import React, { Component } from 'react'
import { withRouter, Link } from 'react-router-dom'
import logic from '../../logic'

import './index.sass'

class Nav extends Component {
   
    state = { isLogin: logic.__userToken__ }

    handleOnLogin = event => {
        event.preventDefault()
        this.props.history.push('/login')

    }


    handleOnRegister = event => {
        event.preventDefault()
        this.props.history.push('/registerOwner')
    }

    handleOnLogout = event => {
        this.setState({isLogin: null})
    }


    render() {
        const isLogin = this.state.isLogin
        let dropdownButton

        if (isLogin) {
            dropdownButton = <div class="btn-group">

                <button type="button" class="btn btn-primary dropdown-toggle" data-toggle="dropdown">
                    <i className="fas fa-paw"></i>
                </button>
                <ul class="dropdown-menu" role="menu">
                    <li><Link to="/registerOwner">Register Owner</Link></li>
                    <li><Link to="/registerPet">Register Pet</Link></li>
                    <li><Link to="/editPassword">Edit Password</Link></li>
                    <li><Link to="/" onClick={this.handleOnLogout}>Logout</Link></li>
                </ul>
            </div>
        }


        return <nav className='navbar navbar-dark bg-dark'>
            <div className='nameVet'>St James Vet</div>
            <div className='navmenu'>
                {!isLogin && <button onClick={this.handleOnLogin} className='btn btn-primary'>Login</button>}
                {isLogin && dropdownButton}
                {this.state.logic__userApiToken__ && dropdownButton}
                <div class="btn-group">
                </div>
            </div>
        </nav>


    }
}

export default withRouter(Nav)
