import React, { Component, Fragment } from 'react'
import { Route, Redirect  } from 'react-router-dom'

import Welcome from '../Welcome'
import Home from '../Home'
import Login from '../Login'
import RegisterOwner from '../RegisterOwner'
import RegisterPet from '../RegisterPet'
import EditOwner from '../EditOwner'
import EditPet from '../EditPet'
import Visit from '../Visit'
import VisitOwner from '../Visit_Owner'
import Appointments from '../Appointments';

import './index.sass'
import logic from '../../logic';

//testejar el helper, cors....

class App extends Component {
  
    state={isLoggedIn: logic.isUserLoggedIn, isAdmin: logic.isAdmin, feedback: ''}

    handleLogin = (email, password) => {
        try {
            logic.logInUser(email, password)
                .then(() =>{
                    this.setState({isLoggedIn: logic.isUserLoggedIn, isAdmin: logic.isAdmin})
                })
                .catch(({message})=> this.setState({ feedback: message }))
            
        } catch ({ message }) {
            this.setState({ feedback: message })
        }
    }
   
    render() {

        const {state: {isLoggedIn, isAdmin}, handleLogin} = this

        return <Fragment>

                <Route path="/welcome" render={() =>  isLoggedIn ? <Home/>: <Welcome/>}/>
                <Route path='/login' render={() => isLoggedIn? <Login onLogin={handleLogin}/> : <Redirect to='/home'/> } />             
                {/* <Route path='/welcome' render={() => isLoggedIn? <Home/>: <Welcome/>}/> */}
                <Route path='/registerOwner' render={() => isAdmin? <RegisterOwner /> : <Redirect to='/'/>}/>

                <Route path='/registerPet' component={RegisterPet}/>

                <Route path='/editOwner' render={()=> isAdmin? <EditOwner /> : <Redirect to='/' /> }/>
                <Route path='/editPet' render={() => isAdmin? <EditPet/> : <Redirect to='/'/>}/>
                <Route path='/visit' render={() => isAdmin? <Visit/> : <Visit to='/'/>}/>
                <Route path='/appointments'  render={() => isAdmin ? <Appointments/> : <Appointments to='/'/>}/>
                <Route path='/visitOwner' render={() => isLoggedIn ? <VisitOwner/> : <VisitOwner to='/'/>}/>
                <Route path='/editOwner' render={() => isAdmin? <EditOwner/> : <EditOwner to='/'/>}/>                
                <Route path= '/home'  render={() => isLoggedIn ? <Home/> : <Home to='/'/>}/> */}
            </Fragment>    
    }
}

export default App
