import React, {Component} from 'react'
import logic from '../../logic'


class EditOwner extends Component {

    state = { name: '', surname: '', idCard: '', phone: '',  adress: '', city: '', email: '', password: '', passwordConfirmation: '', error: null }
    
    handleOnChange = ({ target: { name, value } }) => this.setState({ [name]: value })
    
    // handleEditProfile = event => {
    //     event.preventDefault()
        
    //     const { state: { name, surname, idCard, phone, adress, city, email, password}, props: { onEditProfile } } = this
    //     const user= JSON.parse(sessionStorage.getItem('user'))
        
    //     this.setState({
    //         name: name || user.name,
    //         surname: surname || user.surname,
    //         idCard: idCard || user.idCard,
    //         phone: phone || user.phone,
    //         adress : adress || user.adress,
    //         city: city || user.city,
    //         email: email || user.email,
    //         password: password || user.password,


            
    //     }, () => {
    //         const { state: { name, surname, idCard, phone, adress, city, email, password}} = this
            
    //         let data={name, surname, idCard, phone, adress, city, email, password}
            
    //         onEditProfile(data)
    //     })
        
    // }
        retrieveUser = async (userId) => {
            const user = await logic.retrieveUser(userId)
            console.log(user)
        }

        componentDidMount() {
            this.retrieveUser()
        }


        handleEditProfile = event => {
            event.preventDefault()
            const { state: { name, surname, idCard, phone, adress, city, email, password} } = this
            this.editProfile(name, surname, idCard, phone, adress, city, email, password)
        }


        editProfile = async (name, surname, idCard, phone, adress, city, email, password ) => {
            try {
                debugger
                //await logic.retrieveUser(name, surname, idCard, phone, adress, city, email, password )
                console.log('dit user!!')
                // this.setState({isRegister: true, error: null})
            } catch ({ message }) {
                this.setState({ error: message })
            }
        }

        render(){

        return <form className="register" onSubmit={this.handleEditProfile} >
        <p className="title_register">Owner's details:</p>
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label">Name</label>
            <div className="col-sm-7">
                <p type="name" name="name" onChange={this.handleOnChange} className="form-control" id="inputEmail3" required></p>
            </div>
        </div>
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label" required>Surname</label>
            <div className="col-sm-7">
                <input type="surname" name="surname" onChange={this.handleOnChange} className="form-control" id="inputPassword3" required></input>
            </div>
        </div>
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label">Id Card</label>
            <div className="col-sm-7">
                <input type="adress" name="idCard" onChange={this.handleOnChange} className="form-control" id="inputEmail3" required></input>
            </div>
        </div>
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label">Phone</label>
            <div className="col-sm-7">
                <input type="phone" name="phone" onChange={this.handleOnChange} className="form-control" id="inputEmail3" required></input>
            </div>
        </div>
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label">Adress</label>
            <div className="col-sm-7">
                <input type="adress" name="adress" onChange={this.handleOnChange} className="form-control" id="inputEmail3" required></input>
            </div>
        </div>
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label">City</label>
            <div className="col-sm-7">
                <input type="city" name="city" onChange={this.handleOnChange} className="form-control" id="inputEmail3" required></input>
            </div>
        </div>
      
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label">Email</label>
            <div className="col-sm-7">
                <input type="email" name="email" onChange={this.handleOnChange} className="form-control" id="inputPassword3" required></input>
            </div>
        </div>
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label">Password</label>
            <div className="col-sm-7">
                <input type="password" name="password" onChange={this.handleOnChange} className="form-control" id="inputEmail3" required></input>
            </div>
        </div>
        <div className="form-group row">
            <label for="validationDefault01" className="col-sm-2 col-form-label">Confirm Password</label>
            <div className="col-sm-7">
                <input type="password" name="passwordConfirmation" onChange={this.handleOnChange} className="form-control" id="inputEmail3" required></input>
            </div>
        </div>
        <button type="submit" className="button">Sign in</button>
        <button className="button" onClick={this.handleGoHome}>GoHome</button>
        {this.state.error && <p className= "feedbackError">{this.state.error}</p>} 
        {this.state.isRegister && <p className= "feedbackOk">You have successfully registered!</p>}
    </form>
    }
}


export default EditOwner
