import React, { Component, useState} from 'react'
import Calendar from '../Calendar'
import logic from '../../logic'
import './index.sass'



class Appointments extends Component {
   
  state = { users: [], pets: [], month:{counter:3},  name: '', microchip: '', petlicence: '', error: null }
    handleOnChange = ({ target: { name, value } }) => this.setState({ [name]: value })

    componentDidMount() {
      this.retrieveUsers()
    }

    handleSelectOwner = async event => {
        event.preventDefault()
        const usersId = event.target.value
        this.retrievePets(usersId)
    }


    retrieveUsers = async () => {
        const users = await logic.retrieveUsers()
        this.setState({ users })
    }


    retrievePets = async userId => {
        const pets = await logic.retrievePets(userId)
        console.log("userId " + userId)
        this.setState({ pets })
    }


    handleGoHome = event => {
      event.preventDefault()
      this.props.history.push('/home')
    }

    handleVisit = event => {
      event.preventDefault()
      const { state: {month ,increment, decrement} } = this
      this.editProfile(month, increment, decrement)
  }

