    increment(){
      this.setState({digit: this.state.month ++})
    }

    onDecrement()
    {
      this.setState({digit: this.state.digit - 1})
    }

    render(){

     return <p> {this.state.digit}
     
     <button type="button" onClick={this.increment}> + </button>
     <button type="button" onClick={this.decrement}> - </button>
     </p>
}
}
